directory_name = 'branch/'

html_template = """
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>{branch}</title>
</head>
<body>
    <h1>Welcome to {branch}</h1>
    <p>This is a template for the {branch} branch.</p>
</body>
</html>
"""
def sanitize_branch_name(branch_name):
    # Replace invalid characters with underscores
    sanitized_name = re.sub(r'[<>:"/\\|?*]', '_', branch_name)
    # Remove trailing and leading spaces
    sanitized_name = sanitized_name.strip()
    return sanitized_name

for branch in branches_list:
    sanitized_branch = sanitize_branch_name(branch)
    file_name = f'{sanitized_branch}.html'
    file_path = os.path.join(directory_name, file_name)
    
    with open(file_path, 'w') as file:
        file.write(html_template.format(branch=branch))
    
    print(f'Created{file_name}')
    